merge	[genericdata].[DataRecordType] as t
using	cte_data as s
on		1=1 and t.[ID] = s.[ID]
when matched then
	update set
	[OldID] = s.[OldID],[Name] = s.[Name],[ParentID] = s.[ParentID],[OldParentID] = s.[OldParentID],[Fields] = s.[Fields]
