iceProxy ();\n\nvar customerContracts = contractServiceProxy.GetCustomerContracts (\"\", ContractId);\nif (customerContracts == null || customerContracts.Count < 1)\n\treturn null;\n\nvar contract = contractServiceProxy.ReadContract (ContractId);\nif (contract == null)\n\treturn null;\n\nvar contractInfo = contractServiceProxy.ReadContractInfo (ContractId);\nif (contractInfo == null)\n\tthrow new NullReferenceException (string.Format (\"No contract info found for contract with ID '{0}'.\", ContractId));\n\nvar contractResources = contractServiceProxy.ReadContractResources (ContractId);\nif (contractResources == null)\n\tthrow new NullReferenceException (string.Format (\"No contract resources found for contract with ID '{0}'.\", ContractId));\n\nif (contractResources.Directories == null || contractResources.Directories.Count == 0)\n\tthrow new NullReferenceException (string.Format (\"No directory number found for contract with ID '{0}'.\", ContractId));\n\nvar installationAddress = contractServiceProxy.GetContractAddress(ContractId);\nif(installationAddress  == null)\n      throw new NullReferenceException (string.Format (\"No installation address found for contract  with ID '{0}'.\", ContractId));\n\n\nvar telephonyContractData = new SOM.ST.Billing.Entities.TelephonyContractData\n{\n\tId = customerContracts[0].Id,\n\tCustomerId = customerContracts[0].CustomerId,\n\tRateplanId = contract.RateplanId,\n\tStatus = customerContracts[0].Status,\n\tActivationDate = customerContracts[0].ActivationDate,\n\tPhoneNumber = contractResources.Directories[0].Number,\n\tStreet = installationAddress.Street ,\n\tCity = installationAddress.City,\n\tIsBlocked = contractInfo.IsBlocked\n};\n\nvar inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\nvar technicalDetails = inventoryServiceProxy.GetTechnicalDetails (contractResources.Directories[0].Number);\n\nif (technicalDetails != null)\n\ttelephonyContractData.LinePathId = technicalDetails.PathId;\n\ntelephonyContra