tracts;\n    }\n\n\npublic List<SOM.ST.Billing.Business.ServiceHistoryRecord> GetContractServiceHistory (string ContractId, string ServiceId) {\n    if (string.IsNullOrEmpty (ContractId))\n        throw new NullReferenceException (\"ContractId\");\n    if (string.IsNullOrEmpty (ServiceId))\n        throw new NullReferenceException (\"ServiceId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var serviceHistoryRecords = contractServiceProxy.ReadContractServiceHistory (ContractId, ServiceId);\n\n    if (serviceHistoryRecords == null || serviceHistoryRecords.Count == 0)\n        return null;\n\n    // var contract = contractServiceProxy.ReadContract(ContractId);\n    // if (contract == null)\n    //     throw new NullReferenceException(string.Format(\"Contract with Id ''{0}'' not found.\", ContractId));\n\n    // var packageId = serviceHistoryRecords[0].PackageId;\n\n    // var services = GetServicesByRateplanAndPackages(contract.RateplanId, packageId);\n    // if (services == null)\n    //     throw new NullReferenceException(string.Format(\"Service with Id ''{0}'' not found.\", serviceHistoryRecords[0].ServiceId));\n\n    // var service = services.FirstOrDefault(item => string.Compare(item.Id, serviceHistoryRecords[0].ServiceId) == 0);\n    // if (service == null)\n    //     throw new NullReferenceException(string.Format(\"Service with Id ''{0}'' not found.\", serviceHistoryRecords[0].ServiceId));\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n\n    var service = commonServiceProxy.ReadServiceDefinition (serviceHistoryRecords[0].ServiceId);\n\n    foreach (var serviceHistoryRecord in serviceHistoryRecords)\n        serviceHistoryRecord.Name = service.Name;\n\n    return serviceHistoryRecords;\n}\n\npublic void BlockContract (string ContractId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    contractServiceProxy.BlockContract (ContractId, true);\n}\n\npublic void UnblockContract (string ContractId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    contractServiceProxy.BlockContract (ContractId, false);\n}\n\n\npublic List<SOM.ST.Billing.Entities.ContractAvailableService> GetContractAvailableServices (SOM.ST.Billing.Entities.GetContractAvailableServicesInput input) {\n    if (input == null)\n        throw new NullReferenceException (\"input\");\n    if (string.IsNullOrEmpty (input.RatePlanId))\n        throw new NullReferenceException (\"input.RatePlanId\");\n    if (string.IsNullOrEmpty (input.ContractId))\n        throw new NullReferenceException (\"input.ContractId\");\n    if (string.IsNullOrEmpty (input.LinePathId))\n        throw new NullReferenceException (\"input.LinePathId\");\n\n    var inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\n    var technicalDetails = inventoryServiceProxy.GetTechnicalDetailsByPath (input.LinePathId);\n    if (technicalDetails == null)\n        throw new NullReferenceException (\"technicalDetails\");\n\n    var switchAvailableServices = inventoryServiceProxy.GetSwitchInfo (technicalDetails.SwitchId);\n\n    var ratePlanServiceProxy = new SOM.ST.Billing.Business.RatePlanServiceProxy ();\n    var allowedServicesByPackage = ratePlanServiceProxy.GetRatePlanAllowedServicesDictionary (input.RatePlanId);\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var existingContractService = commonServiceProxy.GetContractServices (input.ContractId);\n\n    var filteredAllowedServices = new List<SOM.ST.Billing.Entities.ContractAvailableService> ();\n\n    if (allowedServicesByPackage != null && allowedServicesByPackage.Count > 0 && switchAvailableServices != null && switchAvailableServices.Count > 0) {\n        //if (allowedServicesByPackage != null && allowedServicesByPackage.Count > 0) {\n        foreach (var allowedServicesKVP in allowedServicesByPackage) {\n            var packageId = allowedServicesKVP.Key;\n            var ratePlanPackageServices = commonServiceProxy.GetServicesDefinitions (input.RatePlanId, packageId);\n\n            foreach (var service in allowedServicesKVP.Value) {\n                if (input.ExcludedPackages != null && input.ExcludedPackages.Contains (packageId))\n                    break;\n\n                //if (!switchAvailableServices.Contains (service))\n                //continue;\n\n                if (existingContractService != null && existingContractService.Any (itm => itm.Id == service && itm.Status != SOM.ST.Billing.Entities.ContractStatus.Deactivate))\n                    continue;\n                var ratePlanService = ratePlanPackageServices.First (item => item.Id == service);\n\n                if (ratePlanService == null)\n                    throw new NullReferenceException (\"ratePlanService\");\n\n                filteredAllowedServices.Add (new SOM.ST.Billing.Entities.ContractAvailableService {\n                    Id = service,\n                        Name = ratePlanService.Name,\n                        PackageId = ratePlanService.PackageId,\n                        NeedsProvisioning = ratePlanService.NeedsProvisioning,\n                        IsNetwork = ratePlanService.IsNetwork,\n                        SubscriptionFee = ratePlanService?.SubscriptionFee,\n                        AccessFee = ratePlanService?.AccessFee\n                });\n            }\n        }\n    }\n\n    return filteredAllowedServices;\n}\n\npublic SOM.ST.Billing.Entities.ContractAddressAndDirectoryInfo GetContractAddressAndDirectoryInfo (string ContractId, string ServiceId) {\n    var result = new SOM.ST.Billing.Entities.ContractAddressAndDirectoryInfo ();\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    result.Address = contractServiceProxy.GetContractAddress (ContractId);\n\n    var contractServices = contractServiceProxy.GetContractServices (ContractId);\n    result.InDirectory = contractServices != null && contractServices.Count > 0 && contractServices.Any (item => string.Compare (item.Id, ServiceId) == 0);\n\n    return result;\n}\n\n\npublic string GetContractLinePathId (string ContractId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n\n    var contractResources = contractServiceProxy.ReadContractResources (ContractId);\n\n    if (contractResources != null && contractResources.Ports != null && contractResources.Ports.Count > 0)\n        return contractResources.Ports[0].Number;\n\n    return null;\n}\n\n\npublic bool IsContractClassified (string PathId) {\n    var inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\n    var technicalDetails = inventoryServiceProxy.GetTechnicalDetailsByPath (PathId);\n    if (technicalDetails == null)\n        throw new NullReferenceException (string.Format (\"No technical details found for contract with Line Path Id ''{0}''.\", PathId));\n\n    return (string.Compare (technicalDetails.PhoneCategory, \"Normal\") != 0);\n}\n\n\npublic string GetContractCategory (string PathId) {\n    var inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\n    var technicalDetails = inventoryServiceProxy.GetTechnicalDetailsByPath (PathId);\n    if (technicalDetails == null)\n        throw new NullReferenceException (string.Format (\"No technical details found for contract with Line Path Id ''{0}''.\", PathId));\n\n    return technicalDetails.PhoneCategory;\n}\n\n\npublic bool IsLastTelephonyContract (string CustomerId, string ContractId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var customerContracts = contractServiceProxy.GetCustomerContracts (CustomerId, null);\n    if (customerContracts == null || customerContracts.Count == 0)\n        throw new NullReferenceException (string.Format (\"No contract found for customer with id ''{0}''.\", CustomerId));\n\n    foreach (var contract in customerContracts) {\n        if (string.Compare (contract.Id, ContractId) == 0)\n            continue;\n        var contractInfo = contractServiceProxy.ReadContractInfo (contract.Id);\n        if (contractInfo == null)\n            throw new NullReferenceException (string.Format (\"No contract info found for contract with id ''{0}''.\", contract.Id));\n\n        if (string.Compare (contractInfo.Type, \"Telephoney\") == 0)\n            return false;\n    }\n\n    return true;\n}\n\npublic SOM.ST.Billing.Entities.ContractInfo GetChildADSLContractRatePlanId (string ContractId) {\n    if (string.IsNullOrEmpty (ContractId))\n        throw new NullReferenceException (\"ContractId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var parentContractInfo = contractServiceProxy.ReadContractInfo (ContractId);\n    if (parentContractInfo == null)\n        throw new NullReferenceException (string.Format (\"Not info found for contract with Id ''{0}''.\", ContractId));\n\n    if (string.IsNullOrEmpty (parentContractInfo.ChildContractId))\n        return null;\n\n    var childContract = contractServiceProxy.ReadContract (parentContractInfo.ChildContractId);\n    if (childContract == null)\n        throw new NullReferenceException (string.Format (\"No contract found with Id ''{0}''.\", parentContractInfo.ChildContractId));\n\n    return new SOM.ST.Billing.Entities.ContractInfo { ContractId = childContract.Id, RatePlanId = childContract.RateplanId };\n}\n\npublic decimal? GetContractBalance (string ContractId) {\n    if (string.IsNullOrEmpty (ContractId))\n        throw new NullReferenceException (\"ContractId\");\n\n    var billingAccountServiceProxy = new SOM.ST.Billing.Business.BillingAccountServiceProxy ();\n    var billingAccountAssignment = billingAccountServiceProxy.ReadBillingAccountAssignment (ContractId, null);\n\n    if (billingAccountAssignment == null)\n        throw new NullReferenceException (string.Format (\"No billing account assignment found for contract with Id ''{0}''.\", ContractId));\n\n    var billingAccount = billingAccountServiceProxy.ReadBillingAccount (billingAccountAssignment.Id);\n\n    if (billingAccount == null)\n        throw new NullReferenceException (string.Format (\"No billing account found with Id ''{0}''.\", billingAccountAssignment.Id));\n\n    return billingAccount.CurrentBalance;\n}\n\n\npublic string GetChildADSLContractId (string ContractId) {\n    if (string.IsNullOrEmpty (ContractId))\n        throw new NullReferenceException (\"ContractId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var parentContractInfo = contractServiceProxy.ReadContractInfo (ContractId);\n    if (parentContractInfo == null)\n        throw new NullReferenceException (string.Format (\"Not info found for contract with Id ''{0}''.\", ContractId));\n\n    return parentContractInfo.ChildContractId;\n}\n\npublic bool IsTelephonyContractHasRelatedADSL (string ContractId) {\n    if (string.IsNullOrEmpty (ContractId))\n        throw new NullReferenceException (\"ContractId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var parentContractInfo = contractServiceProxy.ReadContractInfo (ContractId);\n    if (parentContractInfo == null)\n        throw new NullReferenceException (string.Format (\"Not info found for contract with Id ''{0}''.\", ContractId));\n\n    return !string.IsNullOrEmpty (parentContractInfo.ChildContractId);\n}\n\n\npublic List<SOM.ST.Billing.Entities.CustomerContractService> GetContractServicesWithExcludedPackages (SOM.ST.Billing.Entities.GetContractServicesInput input) {\n    if (input == null)\n        throw new NullReferenceException (\"input\");\n\n    if (string.IsNullOrEmpty (input.ContractId))\n        throw new NullReferenceException (\"input.ContractId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var contractServices = contractServiceProxy.GetContractServices (input.ContractId);\n\n    if (contractServices == null || contractServices.Count == 0)\n        return null;\n\n    var contract = contractServiceProxy.ReadContract (input.ContractId);\n    if (contract == null)\n        throw new NullReferenceException (string.Format (\"Contract with Id ''{0}'' not found.\", input.ContractId));\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var servicesManager  = new SOM.ST.Billing.Business.ServicesManager();\n    var packages = contractServices.Select (item => item.PackageId).Distinct ().ToList ();\n    var filteredPackages = (input.ExcludedPackages != null && input.ExcludedPackages.Count > 0) ? packages.Except (input.ExcludedPackages) : packages;\n    var services = servicesManager.GetServicesByRateplanAndPackages (new SOM.ST.Billing.Entities.GetServicesByRateplanAndPackagesInput { RatePlanId = contract.RateplanId, Packages = (filteredPackages != null) ? filteredPackages.ToList () : new List<string> () });\n\n    var result = new List<SOM.ST.Billing.Entities.CustomerContractService> ();\n    foreach (var contractService in contractServices) {\n        if (contractService.Status == SOM.ST.Billing.Entities.ContractStatus.Deactivate)\n            continue;\n\n        if (input.ExcludedPackages != null && input.ExcludedPackages.Contains (contractService.PackageId))\n            continue;\n\n        var service = services.FirstOrDefault (item => string.Compare (item.Id, contractService.Id) == 0);\n        contractService.Name = service.Name;\n        result.Add (contractService);\n    }\n\n    return result;\n}\n\n\npublic SOM.ST.Billing.Entities.TelephonyContractData GetTelephonyContract (string contractId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n\n    var customerContracts = contractServiceProxy.GetCustomerContracts (\"\", contractId);\n    if (customerContracts == null || customerContracts.Count < 1)\n        return null;\n\n    var contract = contractServiceProxy.ReadContract (contractId);\n    if (contract == null)\n        return null;\n\n    var contractInfo = contractServiceProxy.ReadContractInfo (contractId);\n    if (contractInfo == null)\n        throw new NullReferenceException (string.Format (\"No contract info found for contract with ID ''{0}''.\", contractId));\n\n    var contractResources = contractServiceProxy.ReadContractResources (contractId);\n    if (contractResources == null)\n        throw new NullReferenceException (string.Format (\"No contract resources found for contract with ID ''{0}''.\", contractId));\n\n    if (contractResources.Directories == null || contractResources.Directories.Count == 0)\n        throw new NullReferenceException (string.Format (\"No directory number found for contract with ID ''{0}''.\", contractId));\n\n    var telephonyContractData = new SOM.ST.Billing.Entities.TelephonyContractData {\n        Id = customerContracts[0].Id,\n        CustomerId = customerContracts[0].CustomerId,\n        RateplanId = contract.RateplanId,\n        Status = customerContracts[0].Status,\n        ActivationDate = customerContracts[0].ActivationDate,\n        PhoneNumber = contractResources.Directories[0].Number,\n        Street = customerContracts[0].Street,\n        City = customerContracts[0].City,\n        IsBlocked = contractInfo.IsBlocked\n    };\n\n    var inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\n    var technicalDetails = inventoryServiceProxy.GetTechnicalDetails (contractResources.Directories[0].Number);\n\n    if (technicalDetails != null)\n        telephonyContractData.LinePathId = technicalDetails.PathId;\n        \n    telephonyContractData.CurrentBalance = GetContractBalance (contractId);\n\n    return telephonyContractData;\n}\n\n\npublic List<SOM.ST.Billing.Entities.CustomerContractService> GetContractServices (string contractId) {\n    if (contractId == null)\n        throw new NullReferenceException (\"ContractId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var contractServices = contractServiceProxy.GetContractServices (contractId);\n\n    if (contractServices == null || contractServices.Count == 0)\n        return null;\n\n    var contract = contractServiceProxy.ReadContract (contractId);\n    if (contract == null)\n        throw new NullReferenceException (string.Format (\"Contract with Id ''{0}'' not found.\", contractId));\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var packages = contractServices.Select (item => item.PackageId).Distinct ().ToList ();\n\n    var servicesManager = new SOM.ST.Billing.Business.ServicesManager();\n    var services = servicesManager.GetServicesByRateplanAndPackages (new SOM.ST.Billing.Entities.GetServicesByRateplanAndPackagesInput { RatePlanId = contract.RateplanId, Packages = packages });\n\n    var filteredServices = new List<SOM.ST.Billing.Entities.CustomerContractService> ();\n    foreach (var contractService in contractServices) {\n        if (contractService.Status == SOM.ST.Billing.Entities.ContractStatus.Deactivate)\n            continue;\n\n        var service = services.FirstOrDefault (item => string.Compare (item.Id, contractService.Id) == 0);\n        contractService.Name = service.Name;\n        filteredServices.Add (contractService);\n    }\n\n    return filteredServices;\n}\n\n}"}}',5,'2019-08-08 09:55:37.713',11),
