('668378D4-4199-4D13-A8FF-8207853D3D6C','22608A4F-BAD6-48C0-97FD-350976253E9A','Customer Manager','{"$type":"Vanrise.Entities.VRNamespaceItemSettings, Vanrise.Entities","Code":{"$type":"Vanrise.Common.MainExtensions.VRDynamicCode.CustomCodeDynamicCodeSettings, Vanrise.Common.MainExtensions","ConfigId":"bdd289df-573c-44a1-9a95-d0de2ed9dd71","CustomCode":"public class CustomerManager {\n\npublic List<SOM.ST.Billing.Entities.TelephonyContract> GetCustomerTelephonyContracts (string CustomerId) {\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n\n    var customerContracts = contractServiceProxy.GetCustomerContracts (CustomerId, \"\");\n    if (customerContracts == null || customerContracts.Count < 1)\n        return null;\n\n    var telephonyCustomerContracts = new List<SOM.ST.Billing.Entities.TelephonyContract> ();\n\n    for (int i = 0; i < customerContracts.Count; i++) {\n        if (customerContracts[i].Status != 2)\n            continue;\n\n        var contractInfo = contractServiceProxy.ReadContractInfo (customerContracts[i].Id);\n        if (contractInfo == null)\n            throw new NullReferenceException (string.Format (\"No contract info found for contract with ID ''{0}''.\", customerContracts[i].Id));\n\n        var contractResources = contractServiceProxy.ReadContractResources (customerContracts[i].Id);\n        if (contractResources == null)\n            throw new NullReferenceException (string.Format (\"No contract resources found for contract with ID ''{0}''.\", customerContracts[i].Id));\n\n        if (contractResources.Directories == null || contractResources.Directories.Count == 0)\n            throw new NullReferenceException (string.Format (\"No directory number found for contract with ID ''{0}''.\", customerContracts[i].Id));\n\n        if (String.Compare (contractInfo.Type, \"Telephony\") == 0 || String.Compare (contractInfo.Type, \"Tel\") == 0) {\n            telephonyCustomerContracts.Add (new SOM.ST.Billing.Entities.TelephonyContract {\n                Id = customerContracts[i].Id,\n                    CustomerId = customerContracts[i].CustomerId,\n                    RateplanId = customerContracts[i].RateplanId,\n                    Status = customerContracts[i].Status,\n                    ActivationDate = customerContracts[i].ActivationDate,\n                    PhoneNumber = contractResources.Directories[0].Number,\n                    IsBlocked = contractInfo.IsBlocked\n            });\n        }\n    }\n\n    for (int i = 0; i < telephonyCustomerContracts.Count; i++) {\n        var contractDetails = contractServiceProxy.ReadContract (telephonyCustomerContracts[i].Id);\n        telephonyCustomerContracts[i].LastStatusChangeDate = contractDetails.LastStatusChangeDate;\n    }\n\n    return telephonyCustomerContracts;\n}\n\n\npublic List<SOM.ST.Billing.Entities.Invoice> GetCustomerInvoices (string CustomerId) {\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var billingAccountServiceProxy = new SOM.ST.Billing.Business.BillingAccountServiceProxy ();\n\n    var documnets = commonServiceProxy.GetFinancialDocuments (CustomerId, \"IN\");\n\n    var result = new List<SOM.ST.Billing.Entities.Invoice> ();\n\n    foreach (var document in documnets) {\n        var invoice = new SOM.ST.Billing.Entities.Invoice {\n            Id = document.Id,\n            DocumentCode = document.DocumentCode,\n            CustomerId = document.CustomerId,\n            DocumentType = document.DocumentType,\n            BillingAccountCode = document.BillingAccountCode,\n            EntryDate = document.EntryDate,\n            DueDate = document.DueDate,\n            Amount = document.Amount,\n            OpenAmount = document.OpenAmount\n        };\n\n        var billingAccountAssinment = billingAccountServiceProxy.ReadBillingAccountAssignment (string.Empty, document.BillingAccountCode);\n\n        if (billingAccountAssinment == null)\n            throw new NullReferenceException (string.Format (\"No billing account assignment with Id {0}\", document.BillingAccountCode));\n\n        invoice.ContractId = billingAccountAssinment.ContractId;\n        var contractResources = contractServiceProxy.ReadContractResources (billingAccountAssinment.ContractId);\n        if (contractResources != null && contractResources.Ports != null && contractResources.Ports.Count > 0)\n            invoice.LinePath = contractResources.Ports[0].Number;\n\n        if (contractResources != null && contractResources.Directories != null && contractResources.Directories.Count > 0)\n            invoice.DirectoryNumber = contractResources.Directories[0].Number;\n\n        result.Add (invoice);\n    }\n\n    return result;\n}\n\n\npublic List<SOM.ST.Billing.Entities.FinancialDocument> GetCustomerDebitAndCreditNotes (string CustomerId) {\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var creditMemos = commonServiceProxy.GetFinancialDocuments (CustomerId, \"CM\");\n    var debitNotes = commonServiceProxy.GetFinancialDocuments (CustomerId, \"DN\");\n\n    var result = new List<SOM.ST.Billing.Entities.FinancialDocument> ();\n\n    if (creditMemos != null && creditMemos.Count > 0)\n        result.AddRange (creditMemos);\n\n    if (debitNotes != null && debitNotes.Count > 0)\n        result.AddRange (debitNotes);\n\n    return result;\n}\n\n\n\npublic decimal? GetCustomerBalance (string customerId) {\n    if (string.IsNullOrEmpty (customerId))\n        throw new NullReferenceException (\"CustomerId\");\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var customerDetails = commonServiceProxy.ReadCustomer (customerId);\n\n    if (customerDetails == null)\n        return null;\n\n    return customerDetails.Balance;\n}\n\n\n\n}"}}',11,'2019-08-08 09:35:58.293',11),
