('70738BF0-FCCD-4309-98CA-91A9216E46E9','22608A4F-BAD6-48C0-97FD-350976253E9A','Services Manager','{"$type":"Vanrise.Entities.VRNamespaceItemSettings, Vanrise.Entities","Code":{"$type":"Vanrise.Common.MainExtensions.VRDynamicCode.CustomCodeDynamicCodeSettings, Vanrise.Common.MainExtensions","ConfigId":"bdd289df-573c-44a1-9a95-d0de2ed9dd71","CustomCode":"public class ServicesManager {\n\n\npublic List<SOM.ST.Billing.Entities.ServiceDefinition> GetServicesByRateplanAndPackages (SOM.ST.Billing.Entities.GetServicesByRateplanAndPackagesInput input) {\n    if (input == null)\n        throw new NullReferenceException (\"input\");\n    if (input.RatePlanId == null)\n        throw new NullReferenceException (\"input.RatePlanId\");\n    if (input.Packages == null)\n        throw new NullReferenceException (\"input.Packages\");\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var result = new List<SOM.ST.Billing.Entities.ServiceDefinition> ();\n\n    for (int i = 0; i < input.Packages.Count; i++) {\n        var listOfServices = commonServiceProxy.GetServicesDefinitions (input.RatePlanId, input.Packages[i]);\n        if (listOfServices != null && listOfServices.Count > 0) {\n            /*if (input.OnlyCoreServices)\n            {\n                var listOfCoreServices = listOfServices.Where(item => item.IsCore);\n                if (listOfCoreServices != null && listOfCoreServices.Any())\n                    result.AddRange(listOfCoreServices);\n            }\n            else*/\n            result.AddRange (listOfServices);\n        }\n    }\n\n    return result;\n}\n\n\npublic bool IsServiceContracted (string contractId, string serviceId) {\n    if (contractId == null)\n        throw new NullReferenceException (\"ContractId\");\n\n    if (serviceId == null)\n        throw new NullReferenceException (\"ServiceId\");\n\n    var contractServiceProxy = new SOM.ST.Billing.Business.ContractServiceProxy ();\n    var contractServices = contractServiceProxy.GetContractServices (contractId);\n\n    if (contractServices == null || contractServices.Count == 0)\n        return false;\n\n    return contractServices.Any (item => string.Compare (item.Id, serviceId) == 0 && item.Status == SOM.ST.Billing.Entities.ContractStatus.Activate);\n}\n\n\n\n}"}}',11,'2019-08-08 09:39:10.303',11),
('356BEBEE-558C-41AB-84F7-952DDFDD8770','22608A4F-BAD6-48C0-97FD-350976253E9A','Rateplan Manager','{"$type":"Vanrise.Entities.VRNamespaceItemSettings, Vanrise.Entities","Code":{"$type":"Vanrise.Common.MainExtensions.VRDynamicCode.CustomCodeDynamicCodeSettings, Vanrise.Common.MainExtensions","ConfigId":"bdd289df-573c-44a1-9a95-d0de2ed9dd71","CustomCode":"public class RateplanManager {\n\npublic List<SOM.ST.Billing.Entities.ServiceDefinition> GetRatePlanServices (SOM.ST.Billing.Entities.GetRatePlanAllowedServicesInput input) {\n    if (input == null)\n        throw new NullReferenceException (\"input\");\n    if (string.IsNullOrEmpty (input.RatePlanId))\n        throw new NullReferenceException (\"input.RatePlanId\");\n    if (string.IsNullOrEmpty (input.SwitchId))\n        throw new NullReferenceException (\"input.SwitchId\");\n\n    var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\n    var ratePlanServiceProxy = new SOM.ST.Billing.Business.RatePlanServiceProxy ();\n    var inventoryServiceProxy = new Inventory.Business.InventoryServiceProxy ();\n\n    var filteredAllowedServices = new List<SOM.ST.Billing.Entities.ServiceDefinition> ();\n\n    var allowedServicesDictionary = ratePlanServiceProxy.GetRatePlanAllowedServicesDictionary (input.RatePlanId);\n    var switchAvailableServices = inventoryServiceProxy.GetSwitchInfo (input.SwitchId);\n\n    if (allowedServicesDictionary != null) // && switchAvailableServices != null && switchAvailableServices.Count > 0)\n    {\n        foreach (var allowedServicesKvp in allowedServicesDictionary) {\n            var packageId = allowedServicesKvp.Key;\n\n            if (input.ExcludedPackages != null && input.ExcludedPackages.Contains (packageId))\n                continue;\n\n            var services = allowedServicesKvp.Value;\n            var ratePlanServices = commonServiceProxy.GetServicesDefinitions (input.RatePlanId, packageId);\n            for (int i = 0; i < services.Count; i++) {\n                //if (!switchAvailableServices.Contains(services[i]))\n                // continue;\n\n                var ratePlanService = ratePlanServices.FirstOrDefault (item => string.Compare (item.Id, services[i]) == 0);\n\n                filteredAllowedServices.Add (new SOM.ST.Billing.Entities.ServiceDefinition {\n                    Id = services[i],\n                        Name = ratePlanService?.Name,\n                        PackageId = packageId,\n                        SubscriptionFee = ratePlanService?.SubscriptionFee,\n                        AccessFee = ratePlanService?.AccessFee\n                });\n            }\n        }\n    }\n    return filteredAllowedServices;\n}\n\n\n\n}"}}',11,'2019-08-07 13:39:29.633',11),
