 null && allowedServicesByPackage.Count > 0 && switchAvailableServices != null && switchAvailableServices.Count > 0) {\n//if (allowedServicesByPackage != null && allowedServicesByPackage.Count > 0) {\n    foreach (var allowedServicesKVP in allowedServicesByPackage) {\n        var packageId = allowedServicesKVP.Key;\n        var ratePlanPackageServices = commonServiceProxy.GetServicesDefinitions (input.RatePlanId, packageId);\n\n        foreach (var service in allowedServicesKVP.Value) {\n            if (input.ExcludedPackages != null && input.ExcludedPackages.Contains (packageId))\n                break;\n\n            if (!switchAvailableServices.Contains (service))\n                continue;\n\n            if (existingContractService != null && existingContractService.Any (itm => itm.Id == service))\n                continue;\n\n            var ratePlanService = ratePlanPackageServices.First (item => item.Id == service);\n\n            if (ratePlanService == null)\n                throw new NullReferenceException (\"ratePlanService\");\n\n            filteredAllowedServices.Add (new SOM.ST.Billing.Entities.ContractAvailableService {\n                Id = service,\n                    Name = ratePlanService.Name,\n                    PackageId = ratePlanService.PackageId,\n                    SubscriptionFee = null,\n                    AccessFee = null,\n                    NeedsProvisioning = ratePlanService.NeedsProvisioning,\n                    IsNetwork = ratePlanService.IsNetwork\n            });\n        }\n    }\n}\n\nreturn filteredAllowedServices;","MethodType":2,"InParameters":{"$type":"System.Collections.Generic.List`1[[Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities]], mscorlib","$values":[{"$type":"Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities","ParameterName":"input","ParameterType":"SOM.ST.Billing.Entities.GetContractAvailableServicesInput"}]},"ReturnType":"List<SOM.ST.Billing.Entities.ContractAvailableService>"},"Se