TelephonyContractInfo> ();\nSOM.ST.Billing.Entities.TelephonyContractInfo pilotContract = null;\n\nfor (int i = 0; i < customerContracts.Count; i++) {\n    if (string.Compare (customerContracts[i].Id, ContractId) == 0) {\n        pilotContract = new SOM.ST.Billing.Entities.TelephonyContractInfo { Id = customerContracts[i].Id, PhoneNumber = customerContracts[i].PhoneNumber };\n        continue;\n    }\n\n    var contractInfo = contractServiceProxy.ReadContractInfo (customerContracts[i].Id);\n\n    if (String.Compare (contractInfo.Type, \"Telephony\") == 0) {\n        telephonyCustomerContracts.Add (new SOM.ST.Billing.Entities.TelephonyContractInfo { Id = customerContracts[i].Id, PhoneNumber = customerContracts[i].PhoneNumber });\n    }\n}\n\nif (pilotContract == null)\n    throw new NullReferenceException (string.Format (\"Contract with id ''{0}'' not found.\", ContractId));\n\nvar pilotTechnicalDetails = inventoryServiceProxy.GetTechnicalDetails (pilotContract.PhoneNumber);\nif (pilotTechnicalDetails == null)\n    throw new NullReferenceException (string.Format (\"Technical Details for phone number ''{0}'' not found.\", pilotContract.PhoneNumber));\n\nvar switchTelephonyContracts = new List<SOM.ST.Billing.Entities.TelephonyContractInfo> ();\n\nfor (int i = 0; i < telephonyCustomerContracts.Count; i++) {\n    var contractTechnicalDetails = inventoryServiceProxy.GetTechnicalDetails (telephonyCustomerContracts[i].PhoneNumber);\n    if (contractTechnicalDetails == null)\n        throw new NullReferenceException (string.Format (\"Technical Details for phone number ''{0}'' not found.\", telephonyCustomerContracts[i].PhoneNumber));\n\n    if (string.Compare (contractTechnicalDetails.SwitchId, pilotTechnicalDetails.SwitchId) == 0) {\n        switchTelephonyContracts.Add (new SOM.ST.Billing.Entities.TelephonyContractInfo { Id = telephonyCustomerContracts[i].Id, PhoneNumber = telephonyCustomerContracts[i].PhoneNumber });\n    }\n}\n\nvar allPoneNumbers = switchTelephonyContracts.Select (item => item.PhoneNumber).ToList ();\nvar deviceDetails = inventoryServiceProxy.GetDeviceIDs (string.Join(\",\",allPoneNumbers));\nvar pilotDeviceDetail = deviceDetails.Find (item => string.Compare (item.PHONE_NUMBER, pilotContract.PhoneNumber)==0);\nvar applicablePhoneNumber = new List<string> ();\n\nfor (int i = 0; i < deviceDetails.Count; i++) {\n    if (string.Compare (deviceDetails[i].DEV_TYPE, pilotDeviceDetail.DEV_TYPE) == 0)\n        applicablePhoneNumber.Add (deviceDetails[i].PHONE_NUMBER);\n}\n\nvar filteredSwitchTelephonyContracts = new List<SOM.ST.Billing.Entities.TelephonyContractInfo> ();\n\nfor (int i = 0; i < switchTelephonyContracts.Count; i++) {\n    if (applicablePhoneNumber.Contains (switchTelephonyContracts[i].PhoneNumber))\n        filteredSwitchTelephonyContracts.Add (new SOM.ST.Billing.Entities.TelephonyContractInfo { Id = switchTelephonyContracts[i].Id, PhoneNumber = switchTelephonyContracts[i].PhoneNumber });\n}\n\nvar result = new List<SOM.ST.Billing.Entities.TelephonyContractInfo> ();\n\nfor (int i = 0; i < filteredSwitchTelephonyContracts.Count; i++) {\n    var contractServices = contractServiceProxy.GetContractServices (filteredSwitchTelephonyContracts[i].Id);\n    if (!contractServices.Any (item => string.Compare (item.Id, PABXServiceId) == 0))\n        result.Add (new SOM.ST.Billing.Entities.TelephonyContractInfo { Id = filteredSwitchTelephonyContracts[i].Id, PhoneNumber = filteredSwitchTelephonyContracts[i].PhoneNumber });\n}\n\nreturn result;","MethodType":1,"InParameters":{"$type":"System.Collections.Generic.List`1[[Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities]], mscorlib","$values":[{"$type":"Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities","ParameterName":"ContractId","ParameterType":"string"},{"$type":"Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities","ParameterName":"PABXServiceId","ParameterType":"string"},{"$type":"Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities","ParameterName":"CustomerId","ParameterType":"string"}]},"ReturnType":"List<SOM.ST.Billing.Entities.TelephonyContractInfo>"},"Security":{"$type":"Vanrise.Entities.VRDynamicAPIMethodSecurity, Vanrise.Entities","RequiredPermissions":{"$type":"Vanrise.Security.Entities.RequiredPermissionSettings, Vanrise.Security.Entities","Entries":{"$type":"System.Collections.Generic.List`1[[Vanrise.Security.Entities.RequiredPermissionEntry, Vanrise.Security.Entities]], mscorlib","$values":[]}}}},{"$type":"Vanrise.Entities.VRDynamicAPIMethod, Vanrise.Entities","VRDynamicAPIMethodId":"00000000-0000-0000-0000-000000000000","Name":"GetPabxServiceParameterValues","Settings":{"$type":"Vanrise.Common.MainExtensions.VRDynamicAPI.VRCustomCodeDynamicAPIMethod, Vanrise.Common.MainExtensions","ConfigId":"4eb2746b-5368-4d05-b6d3-efd075be2dcf","MethodBody":"var commonServiceProxy = new SOM.ST.Billing.Business.CommonServiceProxy ();\nvar serviceParameters = commonServiceProxy.GetServiceParameters (ServiceId);\n\nif (serviceParameters != null && serviceParameters.Count > 0)\n    return serviceParameters[0].Values;\n\nreturn null;","MethodType":1,"InParameters":{"$type":"System.Collections.Generic.List`1[[Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities]], mscorlib","$values":[{"$type":"Vanrise.Entities.VRDynamicAPIMethodParameter, Vanrise.Entities","ParameterName":"ServiceId","ParameterType":"string"}]},"ReturnType":"List<SOM.ST.Billing.Entities.ServiceParameterValue>"},"Security":{"$type":"Vanrise.Entities.VRDynamicAPIMethodSecurity, Vanrise.Entities","RequiredPermissions":{"$type":"Vanrise.Security.Entities.RequiredPermissionSettings, Vanrise.Security.Entities","Entries":{"$type":"System.Collections.Generic.List`1[[Vanrise.Security.Entities.RequiredPermissionEntry, Vanrise.Security.Entities]], mscorlib","$values":[]}}}},{"$type":"Vanrise.Entities.VRDynamicAPIMethod, Vanrise.Entities","VRDynamicAPIMethodId":"00000000-0000-0000-0000-000000000000","Name":"GetPabxContract","S